import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { Card, Button } from 'react-native-paper';
import { useDatabase } from '../../../DatabaseContext';

const SimpleCalendarScreen = () => {
  const { isDbReady } = useDatabase();

  return (
    <View style={styles.container}>
      <Card style={styles.card}>
        <Card.Content>
          <Text style={styles.title}>Календарь операций</Text>
          <Text style={styles.subtitle}>Статус БД: {isDbReady ? 'Готова' : 'Инициализация...'}</Text>
          <Button 
            mode="contained" 
            style={styles.button}
            onPress={() => alert('Кнопка нажата')}
          >
            Тестовая кнопка
          </Button>
        </Card.Content>
      </Card>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#F5F5F5',
  },
  card: {
    marginBottom: 16,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  subtitle: {
    fontSize: 16,
    color: '#757575',
    marginBottom: 20,
  },
  button: {
    marginTop: 10,
  }
});

export default SimpleCalendarScreen;
