// App.tsx - с интегрированными экранами, включая SimpleAnimalListScreen
import React, { createContext, useContext, useState, useEffect } from 'react';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { View, Text } from 'react-native';
import { StatusBar } from 'expo-status-bar';
import { Provider as PaperProvider, Button } from 'react-native-paper';
import { DefaultTheme } from 'react-native-paper';
import { MaterialIcons } from '@expo/vector-icons';

// Импорт реальных экранов
import SettingsScreen from './src/screens/settings/SettingsScreen';
import DirectoriesScreen from './src/screens/directories/DirectoriesScreen';
import SimpleAnimalListScreen from './src/screens/animals/SimpleAnimalListScreen';

// Простая тема
const theme = {
  ...DefaultTheme,
  colors: {
    ...DefaultTheme.colors,
    primary: '#2196F3',
  },
};

// Очень простой DatabaseContext
const DatabaseContext = createContext({ isDbReady: false });
export const useDatabase = () => useContext(DatabaseContext);

// Очень простой DatabaseProvider
const DatabaseProvider = ({ children }) => {
  const [isDbReady, setIsDbReady] = useState(false);
  
  useEffect(() => {
    // Имитация инициализации БД
    setTimeout(() => {
      console.log('БД инициализирована');
      setIsDbReady(true);
    }, 1500);
  }, []);
  
  return (
    <DatabaseContext.Provider value={{ isDbReady }}>
      {children}
    </DatabaseContext.Provider>
  );
};

// Компонент-заглушка с информацией о статусе БД
const StatusScreen = ({ name }) => {
  const { isDbReady } = useDatabase();
  
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text style={{ fontSize: 24, marginBottom: 20 }}>{name}</Text>
      <Text style={{ marginBottom: 20 }}>
        Статус БД: {isDbReady ? 'Готова' : 'Инициализация...'}
      </Text>
      <Button mode="contained" onPress={() => alert(`Действие на экране ${name}`)}>
        Тестовая кнопка
      </Button>
    </View>
  );
};

// Заглушки для стеков навигации
const CalendarScreen = () => <StatusScreen name="Календарь" />;
const ExportScreen = () => <StatusScreen name="Экспорт" />;

// Создаем стеки навигации
const AnimalStack = createNativeStackNavigator();
const CalendarStack = createNativeStackNavigator();
const DirectoriesStack = createNativeStackNavigator();
const ExportStack = createNativeStackNavigator();
const SettingsStack = createNativeStackNavigator();

const AnimalStackNavigator = () => (
  <AnimalStack.Navigator>
    <AnimalStack.Screen 
      name="AnimalList" 
      component={SimpleAnimalListScreen} 
      options={{ title: 'Животные' }} 
    />
  </AnimalStack.Navigator>
);

const CalendarStackNavigator = () => (
  <CalendarStack.Navigator>
    <CalendarStack.Screen 
      name="Calendar" 
      component={CalendarScreen} 
      options={{ title: 'Календарь' }} 
    />
  </CalendarStack.Navigator>
);

const DirectoriesStackNavigator = () => (
  <DirectoriesStack.Navigator>
    <DirectoriesStack.Screen 
      name="Directories" 
      component={DirectoriesScreen} 
      options={{ title: 'Справочники' }} 
    />
  </DirectoriesStack.Navigator>
);

const ExportStackNavigator = () => (
  <ExportStack.Navigator>
    <ExportStack.Screen 
      name="Export" 
      component={ExportScreen} 
      options={{ title: 'Экспорт' }} 
    />
  </ExportStack.Navigator>
);

const SettingsStackNavigator = () => (
  <SettingsStack.Navigator>
    <SettingsStack.Screen 
      name="Settings" 
      component={SettingsScreen} 
      options={{ title: 'Настройки' }} 
    />
  </SettingsStack.Navigator>
);

// Нижняя навигация
const Tab = createBottomTabNavigator();

const MainNavigator = () => {
  return (
    <Tab.Navigator
      screenOptions={{
        tabBarActiveTintColor: '#2196F3',
        tabBarInactiveTintColor: '#757575',
        headerShown: false
      }}
    >
      <Tab.Screen 
        name="Animals" 
        component={AnimalStackNavigator} 
        options={{
          tabBarLabel: 'Животные',
          tabBarIcon: ({ color, size }) => (
            <MaterialIcons name="pets" color={color} size={size} />
          )
        }}
      />
      <Tab.Screen 
        name="Calendar" 
        component={CalendarStackNavigator} 
        options={{
          tabBarLabel: 'Календарь',
          tabBarIcon: ({ color, size }) => (
            <MaterialIcons name="event" color={color} size={size} />
          )
        }}
      />
      <Tab.Screen 
        name="Directories" 
        component={DirectoriesStackNavigator} 
        options={{
          tabBarLabel: 'Справочники',
          tabBarIcon: ({ color, size }) => (
            <MaterialIcons name="menu-book" color={color} size={size} />
          )
        }}
      />
      <Tab.Screen 
        name="Export" 
        component={ExportStackNavigator} 
        options={{
          tabBarLabel: 'Экспорт',
          tabBarIcon: ({ color, size }) => (
            <MaterialIcons name="cloud-download" color={color} size={size} />
          )
        }}
      />
      <Tab.Screen 
        name="Settings" 
        component={SettingsStackNavigator} 
        options={{
          tabBarLabel: 'Настройки',
          tabBarIcon: ({ color, size }) => (
            <MaterialIcons name="settings" color={color} size={size} />
          )
        }}
      />
    </Tab.Navigator>
  );
};

export default function App() {
  return (
    <SafeAreaProvider>
      <PaperProvider theme={theme}>
        <DatabaseProvider>
          <NavigationContainer>
            <MainNavigator />
            <StatusBar style="auto" />
          </NavigationContainer>
        </DatabaseProvider>
      </PaperProvider>
    </SafeAreaProvider>
  );
}
