import React from 'react';
import { View, ScrollView, StyleSheet, Alert } from 'react-native';
import { Text, Card, Button, List, Divider } from 'react-native-paper';
import { useDatabase } from '../../../DatabaseContext';

const SimpleExportScreen = () => {
  const { isDbReady } = useDatabase();
  
  const exportAnimals = () => {
    Alert.alert('Информация', 'Экспорт животных будет доступен после подключения настоящей базы данных');
  };
  
  const exportOperations = () => {
    Alert.alert('Информация', 'Экспорт операций будет доступен после подключения настоящей базы данных');
  };

  return (
    <ScrollView style={styles.container}>
      <Card style={styles.card}>
        <Card.Content>
          <Text style={styles.sectionTitle}>Статус приложения</Text>
          <View style={styles.statusContainer}>
            <Text style={styles.statusLabel}>База данных:</Text>
            <Text style={[
              styles.statusValue, 
              { color: isDbReady ? '#4CAF50' : '#F44336' }
            ]}>
              {isDbReady ? 'Готова' : 'Инициализация...'}
            </Text>
          </View>
        </Card.Content>
      </Card>
      
      <Card style={styles.card}>
        <Card.Content>
          <Text style={styles.sectionTitle}>Экспорт списка животных</Text>
          
          <Text style={styles.description}>
            Экспортировать полный список животных со всеми данными в Excel
          </Text>
          
          <Button
            mode="contained"
            onPress={exportAnimals}
            style={styles.exportButton}
            icon="file-excel"
            disabled={!isDbReady}
          >
            Экспортировать список животных
          </Button>
        </Card.Content>
      </Card>
      
      <Card style={styles.card}>
        <Card.Content>
          <Text style={styles.sectionTitle}>Экспорт операций</Text>
          
          <Text style={styles.description}>
            Экспортировать операции за выбранный период времени
          </Text>
          
          <List.Section>
            <List.Item
              title="Операции за сегодня"
              left={props => <List.Icon {...props} icon="calendar-today" />}
              onPress={exportOperations}
            />
            <Divider />
            <List.Item
              title="Операции за неделю"
              left={props => <List.Icon {...props} icon="calendar-week" />}
              onPress={exportOperations}
            />
            <Divider />
            <List.Item
              title="Операции за месяц"
              left={props => <List.Icon {...props} icon="calendar-month" />}
              onPress={exportOperations}
            />
          </List.Section>
        </Card.Content>
      </Card>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5F5F5',
  },
  card: {
    margin: 16,
    marginBottom: 8,
    elevation: 2,
  },
  sectionTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 16,
    color: '#424242',
  },
  description: {
    fontSize: 14,
    color: '#757575',
    marginBottom: 16,
  },
  statusContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 8,
  },
  statusLabel: {
    fontSize: 16,
    marginRight: 8,
    color: '#616161',
  },
  statusValue: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  exportButton: {
    marginTop: 8,
  },
});

export default SimpleExportScreen;
